import React, { useState, useEffect } from 'react';

const AlkuEräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data);
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };

  const calculateCombinedTimesByRound = () => {
    const combinedTimesByRound = {};

    times.forEach((time) => {
      const teamId = time.joukkue_id;
      const round = time.era;

      if (!combinedTimesByRound[round]) {
        combinedTimesByRound[round] = {};
      }

      if (!combinedTimesByRound[round][teamId]) {
        combinedTimesByRound[round][teamId] = 0;
      }

      combinedTimesByRound[round][teamId] += time.aika;
    });

    return combinedTimesByRound;
  };

  // Placeholder implementation, replace with your actual logic
const findRoundWinners = (eraTeams) => {
  const roundWinners = [];

  // Your logic to determine winners in the current era
  // For example, let's assume the team with the lowest combined time is the winner
  const sortedTeams = Object.entries(eraTeams).sort(([, timeA], [, timeB]) => timeA - timeB);

  // Assuming the first team is the winner
  const [winnerTeamId, winnerCombinedTime] = sortedTeams[0];
  roundWinners.push({ teamId: winnerTeamId, combinedTime: winnerCombinedTime });

  return roundWinners;
};


  const findWinnersByRound = () => {
    const combinedTimesByRound = calculateCombinedTimesByRound();
    let allTeams = [];
    let allTeamsByEra = {};
  
    // Step 1: Get all teams and their combined times
    times.forEach((time) => {
      const teamId = time.joukkue_id;
  
      if (!allTeams[teamId]) {
        allTeams[teamId] = 0;
      }
  
      allTeams[teamId] += time.aika;
  
      // Step 2: Get all teams and their combined times by Era
      const era = time.era;
      if (!allTeamsByEra[era]) {
        allTeamsByEra[era] = {};
      }
  
      if (!allTeamsByEra[era][teamId]) {
        allTeamsByEra[era][teamId] = 0;
      }
  
      allTeamsByEra[era][teamId] += time.aika;
    });
  
    let allWinners = [];
  
    // Step 3 and 4: Get winners in each era and exclude them from allTeams
    Object.keys(allTeamsByEra).forEach((era) => {
      const eraTeams = allTeamsByEra[era];
      const eraWinners = findRoundWinners(eraTeams);
      allWinners.push(...eraWinners);
  
      // Exclude winners from allTeams
      eraWinners.forEach((winner) => delete allTeams[winner.teamId]);
    });
  
    // Step 5: Get 6 best combined times from remaining teams in allTeams
    const remainingTeams = Object.keys(allTeams).map((teamId) => ({
      teamId,
      combinedTime: allTeams[teamId],
    }));
  
    const sortedRemainingTeams = remainingTeams.sort((a, b) => a.combinedTime - b.combinedTime);
    const sixBestRemainingTeams = sortedRemainingTeams.slice(0, 6);
  
    // Step 6: Add 6 best remaining teams to Winners
    allWinners.push(...sixBestRemainingTeams);
  
    console.log('Winners:', allWinners);
    console.log('All Teams:', remainingTeams);
  };
  
  
  
  

  const handleLopetaClick = async () => {
    try {
      const { allWinners, remainingTeams } = findWinnersByRound();
  
      console.log('Winners:', allWinners);
      console.log('All Teams:', remainingTeams);
    } catch (error) {
      console.error('Error handling lopeta click:', error);
    }
  };
  
  
  

  return (
    <div>
      <button onClick={handleLopetaClick}>Lopeta erä</button>
    </div>
  );
};

export default AlkuEräLopetus;
