import React, { useState, useEffect } from 'react';
import CompetitionFetch from './CompetitionFetch';
import '../css/CompetitionAlkuerä.css'; // Import the CSS file
import { sendSelectedGroupData } from './WebSocketUtils';

function CompetitionAlkuerä() {
  const [teams, setTeams] = useState([]);
  const [groupedTeams, setGroupedTeams] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const socket = new WebSocket('ws://localhost:3002'); // Replace with the correct WebSocket URL

  const handleDataFetched = (data) => {
    const sortedTeams = [...data].sort((a, b) => a.osallistumis_nro - b.osallistumis_nro);
    const first36Teams = sortedTeams.slice(0, 36);
    setTeams(first36Teams);
    divideTeamsIntoGroups(first36Teams);
  };

  const divideTeamsIntoGroups = (teams) => {
    const grouped = [];
    for (let i = 0; i < teams.length; i += 6) {
      grouped.push(teams.slice(i, i + 6));
    }
    setGroupedTeams(grouped);
  };

  const handleGroupSelection = (selectedGroupIndex) => {
    setSelectedGroup(selectedGroupIndex);
    
    // Prepare the selected group data
    const selectedGroupData = groupedTeams[selectedGroupIndex]; // Replace with your data structure
    console.log('Selected group data:', selectedGroupData);
    
    // Send the selected group data to the WebSocket server
    sendSelectedGroupData(selectedGroupData);
  }
  

  useEffect(() => {
    // Retrieve the selected group from localStorage when the component mounts
    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      setSelectedGroup(JSON.parse(storedSelectedGroup));
    }
  }, []);

  return (
    <div>
      <h1>Competition Alkuerä</h1>
      <div className="groups-container">
        {groupedTeams.map((group, groupIndex) => (
          <div key={groupIndex} className="group">
            <h2>Alkuerä {groupIndex + 1}</h2>
            <button onClick={() => handleGroupSelection(groupIndex)}>Select</button>
            {group.map((team) => (
              <div key={team.joukkue_id} className="team">
                {team.joukkue_nimi}
              </div>
            ))}
          </div>
        ))}
      </div>
      <CompetitionFetch onDataFetched={handleDataFetched} />
    </div>
  );
}

export default CompetitionAlkuerä;
