import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';

function Ajastin() {
  const { teamId } = useParams();
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    document.title = `Team ${teamId}`;
    const newSocket = io('http://localhost:8000');

    newSocket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    newSocket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    newSocket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);

      localStorage.setItem('selectedGroup', updatedGroupData);
    });

    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
    }

    setSocket(newSocket);

    return () => {
      if (newSocket.connected) {
        newSocket.disconnect();
      }
    };
  }, [teamId]);

  useEffect(() => {
    const timers = {};
    selectedGroup?.selectedTasks.forEach((task) => {
      timers[task.tehtävä_nimi] = {
        elapsedTime: 0,
        intervalId: null,
      };
    });
    setElapsedTimes(timers);

    return () => {
      Object.values(timers).forEach((timer) => clearInterval(timer.intervalId));
    };
  }, [selectedGroup]);

  useEffect(() => {
    if (isRunning) {
      const intervalIds = Object.keys(elapsedTimes).map((taskName) => {
        return setInterval(() => {
          setElapsedTimes((prevElapsedTimes) => ({
            ...prevElapsedTimes,
            [taskName]: {
              ...prevElapsedTimes[taskName],
              elapsedTime: prevElapsedTimes[taskName].elapsedTime + 1,
            },
          }));
        }, 10);
      });

      return () => {
        intervalIds.forEach((intervalId) => clearInterval(intervalId));
      };
    }
  }, [isRunning, elapsedTimes]);

  useEffect(() => {
    return () => {
      Object.values(elapsedTimes).forEach((timer) => {
        clearInterval(timer.intervalId);
      });
    };
  }, []);

  return (
    <div>
      <h2>{`Team ${teamId} Stopwatch`}</h2>
      {selectedGroup && (
        <div>
          <h3>Selected Tasks:</h3>
          <ul>
            {selectedGroup.selectedTasks.map((task) => (
              <li key={task.tehtävä_nimi}>
                {task.tehtävä_nimi}: {formatTime(task.max_aika * 100)}
                <div className="timer">
                  <span>{formatTime(elapsedTimes[task.tehtävä_nimi]?.elapsedTime * 10)}</span>
                </div>
                <div className="controls">
                  <button onClick={() => startTimer(task.tehtävä_nimi)}>Start</button>
                  <button onClick={() => stopTimer(task.tehtävä_nimi)}>Stop</button>
                  <button onClick={() => resetTimer(task.tehtävä_nimi)}>Reset</button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Ajastin;
