import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TasksEdit({ taskId, onClose, onUpdateTask }) {
  const [taskData, setTaskData] = useState({
    tehtävä_nimi: '',
    max_aika: '',
  });

  // Fetch the task details when the component mounts
  useEffect(() => {
    // Make an API request to get task details by ID
    axios.get(`http://localhost:3001/api/tehtavat/${taskId}`)
      .then((response) => {
        const { tehtävä_nimi, max_aika } = response.data;
        setTaskData({
          tehtävä_nimi,
          max_aika,
        });
      })
      .catch((error) => {
        console.error('Error fetching task details:', error);
      });
  }, [taskId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTaskData({
      ...taskData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Make an API request to update the task details
    axios.put(`http://localhost:3001/api/tehtavat/${taskId}`, taskData)
      .then((response) => {
        console.log('Task updated:', response.data);
        onUpdateTask(response.data); // Notify the parent component of the update
        onClose(); // Close the edit form
      })
      .catch((error) => {
        console.error('Error updating task:', error);
      });
  };

  const handleDelete = () => {
    // Make an API request to delete the task
    axios.delete(`http://localhost:3001/api/tehtavat/${taskId}`)
      .then(() => {
        console.log('Task deleted');
        onDeleteTask(taskId); // Notify the parent component of the deletion
        onClose(); // Close the edit form
      })
      .catch((error) => {
        console.error('Error deleting task:', error);
      });
  };

  return (
    <div className="tasks-edit-container">
      <h2>Edit Task</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="tehtävä_nimi">Task Name</label>
          <input
            type="text"
            id="tehtävä_nimi"
            name="tehtävä_nimi"
            value={taskData.tehtävä_nimi}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="max_aika">Max Time</label>
          <input
            type="text"
            id="max_aika"
            name="max_aika"
            value={taskData.max_aika}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Update Task</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
}

export default TasksEdit;
