import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function Competition() {
  const [selectedGroupData, setSelectedGroupData] = useState({
    selectedGroup: null,
    finishedTimes: {},
  });

  useEffect(() => {
    document.title = 'Kilpailu';
    const socket = io('http://localhost:8000');

    socket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    socket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    socket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroupData((prevData) => ({
        ...prevData,
        selectedGroup: parsedData.selectedGroup,
      }));
    });

    socket.on('finishedTimeUpdate', (updatedFinishedTimes) => {
      console.log('Received updated finished times:', updatedFinishedTimes);
      setSelectedGroupData((prevData) => ({
        ...prevData,
        finishedTimes: updatedFinishedTimes.finishedTimes,
      }));
    });

    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, []);

  const selectedGroup = selectedGroupData.selectedGroup;
  const finishedTimes = selectedGroupData.finishedTimes;

  return (
    <div>
      <h1>Competition</h1>
      {selectedGroup ? (
        <div>
          <h2>{selectedGroup.groupInfo}</h2>
          <h3>Selected Task: {selectedGroup.selectedTask ? selectedGroup.selectedTask.tehtävä_nimi : 'None'}</h3>
          <p>Max Aika: {selectedGroup.selectedTask ? selectedGroup.selectedTask.max_aika : 'N/A'}</p>

          <ul>
            {selectedGroup.teams.map((team) => (
              <li key={team.joukkue_id}>
                <h3>{team.joukkue_nimi}</h3>
                <p>Members: {team.jäsenet}</p>
                <p>Finished Time: {finishedTimes[team.joukkue_id] || 'Not available'}</p>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
