import React, { useState, useEffect } from 'react';
import axios from 'axios';

function JoukkueList() {
  const [teams, setTeams] = useState([]);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    // Fetch the initial list of teams from the server
    axios.get('http://localhost:3001/api/joukkueet')
      .then((response) => {
        setTeams(response.data);
      })
      .catch((error) => {
        console.error('Error fetching teams:', error);
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:3001/api/joukkueet', formData)
      .then((response) => {
        // Add the new team to the state
        setTeams([...teams, response.data]);
        setFormData({});
      })
      .catch((error) => {
        console.error('Error creating team:', error);
      });
  };

  const handleDelete = (teamId) => {
    axios.delete(`http://localhost:3001/api/joukkueet/${teamId}`)
      .then(() => {
        setTeams(teams.filter(team => team.joukkue_id !== teamId));
      })
      .catch((error) => {
        console.error('Error deleting team:', error);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
      </form>
      <ul>
        {teams.map(team => (
          <li key={team.joukkue_id}>
            {team.joukkue_nimi}
            <button onClick={() => handleDelete(team.joukkue_id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default JoukkueList;
