import React, { useState, useEffect } from 'react';

const AlkuEräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data);
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };

  const groupTimesByEra = (times, eraName) => {
    const filteredTimes = times.filter((time) => time.era.includes(eraName));
  
    const groupedTimes = {};
    filteredTimes.forEach((time) => {
      const eraNumber = time.era.match(/\d+/);
      if (eraNumber) {
        const key = `Era ${eraNumber[0]}`;
        if (!groupedTimes[key]) {
          groupedTimes[key] = [];
        }
        groupedTimes[key].push(time);
      }
    });
  
    return groupedTimes;
  };
  

  const handleLopetaClick = async () => {
    try {
      await fetchTimes();
  
      if (times && times.length > 0) {
        const groupedByEra = groupTimesByEra(times, 'Alkuerä');
        console.log('Grouped by era:', groupedByEra);
  
        // Determine the winner for each group
        const winners = {};
        Object.keys(groupedByEra).forEach((eraKey) => {
          const group = groupedByEra[eraKey];
          // Assuming there is a property called 'time' in each time object
          const winner = findWinner(group);
          winners[eraKey] = winner;
        });
  
        console.log('Winners:', winners);
  
        // Create a new list excluding the winners
        const nonWinners = [];
        Object.keys(groupedByEra).forEach((eraKey) => {
          const group = groupedByEra[eraKey];
          const winner = winners[eraKey];
          const nonWinnerGroup = group.filter((time) => time !== winner);
          nonWinners.push(...nonWinnerGroup);
        });
  
        console.log('Non-winners:', nonWinners);
      } else {
        alert('No times available.');
      }
    } catch (error) {
      console.error('Error handling lopeta click:', error);
    }
  };
  
  
  // Function to find the winner in a group based on a certain criteria
  const findWinner = (group) => {
    // Assuming there is a property called 'time' in each time object
    return group.reduce((winner, current) => {
      return current.time < winner.time ? current : winner;
    });
  };
  
  
  
console.log(times)
  return (
    <div>
      <button onClick={handleLopetaClick}>Lopeta erä</button>
    </div>
  );
};

export default AlkuEräLopetus;
