import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TasksSelector({ onSelectTask }) {
  const [tasks, setTasks] = useState([]);
  const [selectedTaskId, setSelectedTaskId] = useState('');

  useEffect(() => {
    axios
      .get('http://localhost:3001/api/tehtavat')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });

    const storedSelectedTaskId = localStorage.getItem('selectedTaskId');
    if (storedSelectedTaskId) {
      setSelectedTaskId(storedSelectedTaskId);
    }
  }, []);

  const handleTaskChange = (e) => {
    const taskId = e.target.value;
    const selected = tasks.find((task) => task.id === parseInt(taskId, 10));

    if (selected) {
      onSelectTask(selected);
      setSelectedTaskId(taskId);
      localStorage.setItem('selectedTaskId', taskId); // Save selected task ID to local storage
    } else {
      console.error('Invalid task selected:', taskId);
    }
  };

  return (
    <div>
      <h2>Task Selector</h2>
      <select value={selectedTaskId} onChange={handleTaskChange}>
        <option key="default" value="0">
          Select a task
        </option>
        {tasks.map((task) => (
          <option key={task.id} value={task.id}>
            {task.tehtävä_nimi}
          </option>
        ))}
      </select>
    </div>
  );
}

export default TasksSelector;
