import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:8000');

const AjastinKello = ({ teamId, tasks, era, maxAika }) => {
  const [lapTimes, setLapTimes] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [elapsedTime, setElapsedTime] = useState(0);
  const intervalIdRef = useRef(null);

  useEffect(() => {
    return () => {
      clearInterval(intervalIdRef.current);
    };
  }, []);

  useEffect(() => {
    const formattedMaxAika = maxAika;

    if (isRunning && elapsedTime >= formattedMaxAika) {
      stopTimer();
    }

    if (isRunning) {
      const intervalId = setInterval(() => {
        setElapsedTime((prevElapsedTime) => prevElapsedTime + 1);
      }, 10);
      intervalIdRef.current = intervalId;
    } else {
      clearInterval(intervalIdRef.current);
    }

    return () => {
      clearInterval(intervalIdRef.current);
    };
  }, [isRunning, teamId, maxAika, elapsedTime]);

  const startTimer = () => {
    setIsRunning(true);
  };

  const stopTimer = () => {
    setIsRunning(false);

    if (elapsedTime > 0) {
      const formattedTime = formatTime(elapsedTime * 10);
      setLapTimes((prevLapTimes) => [...prevLapTimes, formattedTime]);
    }

    resetTimer();
  };

  const resetTimer = () => {
    setElapsedTime(0);
    clearInterval(intervalIdRef.current);
  };

  function formatTime(milliseconds) {
    // Function unchanged
  }

  const postLapTimeToMySQL = async (era, tehtava, teamId, lapTime) => {
    const selectedGroup = localStorage.getItem('selectedGroup');

    if (selectedGroup) {
      try {
        const parsedData = JSON.parse(selectedGroup);
        console.log('teamId:', teamId);
        const response = await fetch('http://localhost:3001/api/aikataulu', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            era: parsedData.groupInfo,
            tehtava,
            joukkueId: teamId,
            aika: lapTime,
          }),
        });

        if (!response.ok) {
          console.error('Failed to send lap time to aikataulu:', response.status);
        }
      } catch (error) {
        console.error('Error sending lap time to aikataulu:', error);
      }
    }
  };

  const handleLapTimeEnd = (tehtava) => {
    const finishedTime = elapsedTime;
    postLapTimeToMySQL(era, tehtava.tehtävä_nimi, teamId, finishedTime);
  };

  return (
    <div>
      <h3>{era}</h3>
      {tasks.map((task, index) => (
        <div key={index}>
          <h4>{task.tehtävä_nimi}</h4>
          <div className="timer">
            <span>{lapTimes.length > index ? lapTimes[index] : formatTime(0)}</span>
          </div>
          <div className="controls">
            <button onClick={startTimer} disabled={isRunning}>
              Start
            </button>
            <button
              onClick={() => {
                stopTimer();
                handleLapTimeEnd(task);
              }}
              disabled={!isRunning}
            >
              Stop
            </button>
            <button onClick={resetTimer}>Reset</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AjastinKello;
