class Register extends Component {
  constructor() {
    super();
    this.state = {
      joukkue_nimi: '',
      jäsenet: '',
      kaupunki: '',
      osallistumis_nro: '',
      semifinalist: false,
      finalist: false,
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (e) => {
    e.preventDefault();

    const newJoukkue = {
      joukkue_nimi: this.state.joukkue_nimi,
      jäsenet: this.state.jäsenet,
      kaupunki: this.state.kaupunki,
      osallistumis_nro: this.state.osallistumis_nro,
      semifinalist: this.state.semifinalist,
      finalist: this.state.finalist,
    };

    // Send a POST request to your API endpoint
    axios.post('/api/joukkueet', newJoukkue)
      .then((response) => {
        console.log(response.data); // Handle the response as needed
      })
      .catch((error) => {
        console.error(error); // Handle any errors
      });
  }

  render() {
    return (
      <div>
        <h2>Register a New Joukkue</h2>
        <form onSubmit={this.handleSubmit}>
          <label>Joukkue Nimi:
            <input type="text" name="joukkue_nimi" onChange={this.handleInputChange} value={this.state.joukkue_nimi} />
          </label>
          <br />
          <label>Jäsenet:
            <input type="text" name="jäsenet" onChange={this.handleInputChange} value={this.state.jäsenet} />
          </label>
          <br />
          <label>Kaupunki:
            <input type="text" name="kaupunki" onChange={this.handleInputChange} value={this.state.kaupunki} />
          </label>
          <br />
          <label>Osallistumis Numero:
            <input type="text" name="osallistumis_nro" onChange={this.handleInputChange} value={this.state.osallistumis_nro} />
          </label>
          <br />
          <label>Semifinalist:
            <input type="checkbox" name="semifinalist" onChange={this.handleInputChange} checked={this.state.semifinalist} />
          </label>
          <br />
          <label>Finalist:
            <input type="checkbox" name="finalist" onChange={this.handleInputChange} checked={this.state.finalist} />
          </label>
          <br />
          <button type="submit">Register</button>
        </form>
      </div>
    );
  }
}

export default Register;
