import React, { useState, useEffect } from 'react';

function CompetitionAlkuerä() {
  const [message, setMessage] = useState(''); // Store received WebSocket messages

  useEffect(() => {
    const server = new WebSocket('wss://localhost:3002'); // Replace with your WebSocket server URL

    server.onopen = () => {
      console.log('WebSocket server connection is open.');
      
      // Send a test message to the server when the connection is open
      server.send('Hello, WebSocket server!');
    };

    server.onmessage = (event) => {
      const receivedMessage = event.data;
      setMessage(receivedMessage);
    };

    return () => {
      if (server && server.readyState === WebSocket.OPEN) {
        server.close();
      }
    };
  }, []); // The empty dependency array ensures this effect runs only once

  return (
    <div>
      <h1>Competition Alkuerä</h1>
      {message && (
        <div>
          <h2>Received Message</h2>
          <p>{message}</p>
        </div>
      )}
    </div>
  );
}

export default CompetitionAlkuerä;
