import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:8000');

const NewAjastinChild = () => {
  const [laps, setLaps] = useState({});
  const intervalIdRef = useRef(null);

  useEffect(() => {
    const lapTimerHandler = (lapTimerData) => {
      const { teamId, taskName, lapTime } = lapTimerData;

      setLaps((prevLaps) => {
        const updatedLaps = { ...prevLaps };

        updatedLaps[teamId] = [...(prevLaps[teamId] || []), { taskName, lapTime }];

        return updatedLaps;
      });
    };

    socket.on('lapTimerUpdate', lapTimerHandler);

    return () => {
      socket.off('lapTimerUpdate', lapTimerHandler);
    };
  }, []);

  const renderTeams = () => {
    return Object.entries(laps).map(([teamId, teamLaps]) => (
      <div key={teamId}>
        <h4>Team: {teamId}</h4>
        <ul>
          {teamLaps.map((lap, index) => (
            <li key={`${teamId}-${index}`}>
              Task: {lap.taskName}, Lap Time: {lap.lapTime} milliseconds
            </li>
          ))}
        </ul>
      </div>
    ));
  };

  return (
    <div>
      <h3>Lap Times</h3>
      <div>
        {renderTeams()}
      </div>
    </div>
  );
};

export default NewAjastinChild;
