import React, { useState, us } from 'react';
import axios from 'axios';

function Register() {
  const [joukkue, setJoukkue] = useState({}); // Initialize as an empty object
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '', // Include "jäsenet"
    kaupunki: '',
    osallistumis_nro: '',
    semifinalist: false,
    finalist: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Create an object with the data to send in the POST request
      const postData = {
        joukkue_nimi: formData.joukkue_nimi,
        jäsenet: formData.jäsenet,
        kaupunki: formData.kaupunki,
        osallistumis_nro: formData.osallistumis_nro,
        semifinalist: formData.semifinalist,
        finalist: formData.finalist,
      };

      // Make a POST request with Axios
      await axios.post('http://localhost:3001/api/joukkueet', postData);

      alert('Data submitted successfully');
    } catch (error) {
      console.error('Error submitting data:', error);
      alert('An error occurred while submitting data');
    }
  };

  useEffect(() => {
    // Fetch joukkue data here and update the state when data is available
  }, []); // Empty dependency array to run once on component mount
  
  console.log(joukkue); // Log the joukkue object
  const { joukkue_nimi, jäsenet, kaupunki, osallistumis_nro, semifinalist, finalist } = joukkue;
  
  return (
    <div>
      <h1>Register</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="joukkue_nimi">Joukkue Nimi:</label>
          <input
            type="text"
            name="joukkue_nimi"
            value={formData.joukkue_nimi}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="jäsenet">Jäsenet:</label>
          <input
            type="text"
            name="jäsenet"
            value={formData.jäsenet}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="kaupunki">Kaupunki:</label>
          <input
            type="text"
            name="kaupunki"
            value={formData.kaupunki}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="osallistumis_nro">Osallistumis Nro:</label>
          <input
            type="number"
            name="osallistumis_nro"
            value={formData.osallistumis_nro}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>
            Semifinalist:
            <input
              type="checkbox"
              name="semifinalist"
              checked={formData.semifinalist}
              onChange={handleChange}
            />
          </label>
        </div>
        <div>
          <label>
            Finalist:
            <input
              type="checkbox"
              name="finalist"
              checked={formData.finalist}
              onChange={handleChange}
            />
          </label>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Register;
