import React, { useState } from 'react';
import axios from 'axios';
import Joukkueet from './Joukkueeet';
import '../css/Register.css';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post('http://localhost:3001/api/joukkueet', formData)
      .then((response) => {
        console.log('Joukkue created:', response.data);
      })
      .catch((error) => {
        console.error('Error creating joukkue:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="register-container">
      <h2>Ilmoittautuminen</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
        <div class="form__group field">
            <input
              type="text"
              class="form__field"
              name="joukkue_nimi"
              placeholder="Name" 
              value={formData.joukkue_nimi}
              onChange={handleInputChange}
              required
            />
            <label class="form__label">Joukkue Nimi</label>
          </div>
          <div class="form__group field">
            <input
              type="text"
              class="form__field"
              name="kaupunki"
              value={formData.kaupunki}
              onChange={handleInputChange}
              required
            />
            <label class="form__label">Kaupunki</label>
          </div>
          <div class="form__group field">
            <input
              type="text"
              class="form__field"
              name="jäsenet"
              value={formData.jäsenet}
              onChange={handleInputChange}
              required
            />
            <label class="form__label">Jäsenet</label>
          </div>
        </div>
        <button type="submit">Submit</button>
      </form>
      <Joukkueet />
      <div class="form__group field">
  <input type="input" class="form__field" placeholder="Name" name="name" id='name' required />
  <label for="name" class="form__label">Name</label>
</div>
    </div>
  );
}

export default Register;
