import React, { useEffect } from 'react';
import { initializeWebSocket, sendSelectedGroupData } from './WebSocketUtils';

// Initialize the WebSocket connection in your component
initializeWebSocket();

// To send data over the WebSocket connection
const selectedGroupData = [{ teamName: 'Team A' }, { teamName: 'Team B' }];
sendSelectedGroupData(selectedGroupData);

function WebSocketTest() {
  useEffect(() => {
    // Initialize the WebSocket connection when the component mounts
    const socket = new WebSocket('ws://localhost:8000');

    socket.onopen = () => {
      console.log('WebSocket connection is open.');
      
      // Send a test message to the server when the connection is open
      socket.send('Hello, WebSocket server!');
    };

    socket.onmessage = (event) => {
      const receivedMessage = event.data;
      console.log('Received message:', receivedMessage);
    };

    // Close the WebSocket connection when the component unmounts
    return () => {
      if (socket && socket.readyState === WebSocket.OPEN) {
        socket.close();
      }
    };
  }, []);

  return (
    <div>
      <h1>WebSocket Test</h1>
    </div>
  );
}

export default WebSocketTest;
