import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function Competition() {
  const [competitionData, setCompetitionData] = useState({
    selectedGroup: null,
    finishedTimes: {},
  });

  useEffect(() => {
    document.title = 'Kilpailu';
    const socket = io('http://localhost:8000');

    socket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    socket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    socket.on('selectedGroupUpdate', (updatedCompetitionData) => {
      console.log('Received updated competition data:', updatedCompetitionData);
      setCompetitionData(updatedCompetitionData);
    });

    socket.on('finishedTimeUpdate', (updatedCompetitionData) => {
      console.log('Received updated finished times:', updatedCompetitionData);
      setCompetitionData(updatedCompetitionData);
    });

    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, []);

  return (
    <div>
      <h1>Competition</h1>
      {competitionData.selectedGroup ? (
        <div>
          <h2>{competitionData.selectedGroup.groupInfo}</h2>
          <h3>Selected Task: {competitionData.selectedGroup.selectedTask ? competitionData.selectedGroup.selectedTask.tehtävä_nimi : 'None'}</h3>
          <p>Max Aika: {competitionData.selectedGroup.selectedTask ? competitionData.selectedGroup.selectedTask.max_aika : 'N/A'}</p>

          <ul>
            {competitionData.selectedGroup.teams.map((team) => (
              <li key={team.joukkue_id}>
                <h3>{team.joukkue_nimi}</h3>
                <p>Members: {team.jäsenet}</p>
                <p>Finished Time: {competitionData.finishedTimes[team.joukkue_id] || 'Not available'}</p>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
