import React, { useState, useEffect } from 'react';
import CompetitionFetch from './CompetitionFetch';
import '../css/CompetitionAlkuerä.css';
import { io } from 'socket.io-client'; // Import the io function

function CompetitionAlkuerä() {
  const [teams, setTeams] = useState([]);
  const [groupedTeams, setGroupedTeams] = useState([]);
  const [selectedGroupIndex, setSelectedGroupIndex] = useState(null); // Track selected group index
  const [socket, setSocket] = useState(null);
  const [previouslySelectedGroupIndex, setPreviouslySelectedGroupIndex] = useState(null);
  const [selectedGroupData, setSelectedGroupData] = useState(null);


  useEffect(() => {
    // Initialize the WebSocket connection when the component mounts
    const newSocket = io('http://localhost:8000');

    newSocket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    newSocket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    setSocket(newSocket);

    return () => {
      // Close the WebSocket connection when the component unmounts
      if (newSocket && newSocket.connected) {
        newSocket.disconnect();
      }
    };
  }, []);

  const handleDataFetched = (data) => {
    const sortedTeams = [...data].sort((a, b) => a.osallistumis_nro - b.osallistumis_nro);
    const first36Teams = sortedTeams.slice(0, 36);
    setTeams(first36Teams);
    divideTeamsIntoGroups(first36Teams);
  };

  const divideTeamsIntoGroups = (teams) => {
    const grouped = [];
    for (let i = 0; i < teams.length; i += 6) {
      grouped.push(teams.slice(i, i + 6));
    }
    setGroupedTeams(grouped);
  };

  const handleGroupSelection = (selectedGroupIndex) => {
    if (selectedGroupIndex === selectedGroupIndex) {
      // Unselect the group if the same group is clicked again
      setSelectedGroupIndex(null);
      setPreviouslySelectedGroupIndex(null);
    } else {
      // Select a new group
      setSelectedGroupIndex(selectedGroupIndex);
      if (previouslySelectedGroupIndex !== null) {
        // Unselect the previously selected group
        setPreviouslySelectedGroupIndex(previouslySelectedGroupIndex);
      }
      setPreviouslySelectedGroupIndex(selectedGroupIndex);
    }

    if (socket && socket.connected) {
      const selectedGroupData = groupedTeams[selectedGroupIndex];
      const jsonData = JSON.stringify(selectedGroupData);
      console.log('Sending selected group data:', jsonData);
      socket.emit('selectedGroup', jsonData);
    }
  }

  return (
    <div>
      <h1>Competition Alkuerä</h1>
      <div className="groups-container">
        {groupedTeams.map((group, groupIndex) => (
          <div
            key={groupIndex}
            className={`group ${groupIndex === selectedGroupIndex ? 'selected' : ''}`}
          >
            <h2>Alkuerä {groupIndex + 1}</h2>
            <button
              onClick={() => handleGroupSelection(groupIndex)}
            >
              {groupIndex === selectedGroupIndex ? 'Unselect' : 'Select'}
            </button>
            {group.map((team) => (
              <div key={team.joukkue_id} className="team">
                {team.joukkue_nimi}
              </div>
            ))}
          </div>
        ))}
      </div>
      <CompetitionFetch onDataFetched={handleDataFetched} />
    </div>
  );
}

export default CompetitionAlkuerä;
