import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TasksSelector({ onSelectTasks }) {
  const [tasks, setTasks] = useState([]);
  const [selectedTaskIds, setSelectedTaskIds] = useState([null, null, null]);

  useEffect(() => {
    axios
      .get('http://localhost:3001/api/tehtavat')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });

    const storedSelectedTaskIds = localStorage.getItem('selectedTaskIds');
    if (storedSelectedTaskIds) {
      setSelectedTaskIds(JSON.parse(storedSelectedTaskIds));
    }
  }, []);

  const handleTaskChange = (index, e) => {
    const taskId = e.target.value;

    // Ensure the task is not already selected
    if (!selectedTaskIds.includes(taskId)) {
      const newSelectedTaskIds = [...selectedTaskIds];
      newSelectedTaskIds[index] = taskId;

      onSelectTasks(newSelectedTaskIds);
      setSelectedTaskIds(newSelectedTaskIds);
      localStorage.setItem('selectedTaskIds', JSON.stringify(newSelectedTaskIds));
    } else {
      console.error('Task already selected:', taskId);
    }
  };

  return (
    <div>
      <h2>Task Selector</h2>
      {[0, 1, 2].map((index) => (
        <select
          key={index}
          value={selectedTaskIds[index] || '0'}
          onChange={(e) => handleTaskChange(index, e)}
        >
          <option key="default" value="0">
            Select a task
          </option>
          {tasks.map((task) => (
            <option key={task.id} value={task.id}>
              {task.tehtävä_nimi}
            </option>
          ))}
        </select>
      ))}
    </div>
  );
}

export default TasksSelector;
