import React, { useState } from 'react';
import axios from 'axios';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send the form data to your API
    axios
      .post('http://localhost:3001/api/joukkueet', formData)
      .then((response) => {
        // Handle the response data, e.g., show a success message or reset the form.
        console.log('Joukkue created:', response.data);
        // You can reset the form or show a success message here.
      })
      .catch((error) => {
        // Handle errors, e.g., show an error message.
        console.error('Error creating joukkue:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div>
      <h2>R</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Joukkue Nimi:
          <input type="text" name="joukkue_nimi" value={formData.joukkue_nimi} onChange={handleInputChange} />
        </label>
        <br/>
        <label>
          Jäsenet:
          <textarea name="jäsenet" value={formData.jäsenet} onChange={handleInputChange} />
        </label>
        <br/>
        <label>
          Kaupunki:
          <input type="text" name="kaupunki" value={formData.kaupunki} onChange={handleInputChange} />
        </label>
        <br/>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Register;
