import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';
import useGroupData from './UseGroupData';

const socket = io('http://localhost:8000');

const NewAjastinChild = () => {
  const [elapsedTime, setElapsedTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [laps, setLaps] = useState({});
  const [lapCount, setLapCount] = useState({});
  const intervalIdRef = useRef(null);
  const { selectedGroup } = useGroupData();

  useEffect(() => {
    const startTimerHandler = (data) => {
      const serverTimestamp = data.timestamp;
      const localTimestamp = window.performance.now();
      const networkDelay = localTimestamp - serverTimestamp;

      const adjustedStartTime = serverTimestamp + networkDelay;

      console.log('Timer started at (adjusted):', adjustedStartTime);

      setStartTime(adjustedStartTime);
      setIsRunning(true);
    };

    const maxAikasArray = selectedGroup?.selectedTasks.map(task => task.max_aika * 1000) || [];

    // ... (existing code)
  
    const lapTimerHandler = (lapTimerData) => {
      console.log('Received lap timer event:', lapTimerData);
  
      const { teamId, taskName } = lapTimerData;
  
      if (!startTime) {
        console.error('Error: startTime is not set.');
        return;
      }
  
      const previousLapsTotalTime = Array.isArray(laps[teamId])
        ? laps[teamId].reduce((totalTime, lap) => totalTime + lap.lapTime, 0)
        : 0;
  
      const existingLapIndex = Array.isArray(laps[teamId])
        ? laps[teamId].findIndex((lap) => lap.taskName === taskName)
        : -1;
  
      let lapTime;
  
      // Calculate the lap time, considering the maximum time for the task
      if (existingLapIndex !== -1) {
        lapTime = elapsedTime * 10 - previousLapsTotalTime + laps[teamId][existingLapIndex].lapTime;
      } else {
        // If no lap has been made, get the max_aika for the corresponding task
        const maxAikaIndex = selectedGroup?.selectedTasks.findIndex(task => task.tehtävä_nimi === taskName);
        const maxAika = maxAikasArray[maxAikaIndex] || 0;
  
        // If lap count is 0, and elapsed time exceeds max_aika, set lap time to max_aika
        if (lapCount[teamId] === 0 && elapsedTime * 10 > maxAika) {
          lapTime = maxAika;
        } else {
          // Otherwise, calculate lap time as usual
          lapTime = elapsedTime * 10 - previousLapsTotalTime;
        }
      }
  
      console.log(`Team: ${teamId}, Task: ${taskName}, Lap Time: ${lapTime} milliseconds`);
  
      setLapCount((prevLapCount) => ({
        ...prevLapCount,
        [teamId]: (prevLapCount[teamId] || 0) + 1,
      }));
  
      setLaps((prevLaps) => {
        const updatedLaps = { ...prevLaps };
  
        if (prevLaps[teamId] && prevLaps[teamId].length >= 3) {
          return prevLaps;
        }
  
        const currentLapCount = prevLaps[teamId] ? prevLaps[teamId].length : 0;
  
        if (existingLapIndex !== -1) {
          updatedLaps[teamId][existingLapIndex] = { taskName, lapTime };
        } else {
          updatedLaps[teamId] = [...(prevLaps[teamId] || []), { taskName, lapTime }];
        }
  
        // Check if all teams have completed the current task
        if (currentLapCount + 1 === 3 && updatedLaps[teamId].length === 3) {
          const combinedTime = updatedLaps[teamId].reduce((totalTime, lap) => totalTime + lap.lapTime, 0);
          console.log(`Team: ${teamId}, Combined Time: ${combinedTime} milliseconds`);
  
          // Increment completedTeams when a team completes three laps
          setCompletedTeams((prevCompletedTeams) => prevCompletedTeams + 1);
        }
  
        return updatedLaps;
      });
    };
  

    socket.on('timerStart', startTimerHandler);
    socket.on('lapTimerUpdate', lapTimerHandler);

    return () => {
      socket.off('timerStart', startTimerHandler);
      socket.off('lapTimerUpdate', lapTimerHandler);
    };
  }, [startTime, elapsedTime, lapCount, laps]);

  useEffect(() => {
    if (isRunning) {
      const intervalId = setInterval(() => {
        setElapsedTime((prevElapsedTime) => prevElapsedTime + 1);
      }, 10);
      intervalIdRef.current = intervalId;
    } else {
      clearInterval(intervalIdRef.current);
    }

    return () => {
      clearInterval(intervalIdRef.current);
    };
  }, [isRunning, elapsedTime]);

  const getCombinedTime = (teamId) => {
    const teamLaps = laps[teamId] || [];
    const combinedTime = teamLaps.reduce((totalTime, lap) => totalTime + lap.lapTime, 0);
    return combinedTime;
  };

  const [completedTeams, setCompletedTeams] = useState(0);

  // Render the teams from selectedGroup
  const renderTeams = () => {
    if (!selectedGroup || !selectedGroup.teams) {
      return <p>No teams available.</p>;
    }
  
    return selectedGroup.teams.map((team) => (
      <div key={team.joukkue_id}>
        <h4>Team: {team.joukkue_nimi}</h4>
        <ul>
          {laps[team.joukkue_id]?.map((lap, index) => (
            <li key={`${team.joukkue_id}-${index}`}>
              Task: {lap.taskName}, Lap Time: {formatTime(lap.lapTime)} milliseconds
            </li>
          ))}
          {laps[team.joukkue_id]?.length === 3 && (
            <li key={`${team.joukkue_id}-combined`}>
              Combined Time: {formatTime(getCombinedTime(team.joukkue_id))}
            </li>
          )}
        </ul>
      </div>
    ));
  };
  

// useEffect to check if all teams have completed their laps
useEffect(() => {
    // Check if all laps are completed
    const allLapsCompleted = selectedGroup?.teams?.every((team) => {
      const teamLaps = laps[team.joukkue_id] || [];
      return teamLaps.length === 3; // Assuming each team needs to complete 3 laps
    });
  
    if (allLapsCompleted) {
      // Stop the timer
      setIsRunning(false);
    }
  }, [selectedGroup, laps]);
  

  return (
    <div>
      <h3>Task Timer</h3>
      <div className="max-times-container">
        <h4>Maximum Time for Each Task:</h4>
        <ul>
          {selectedGroup?.selectedTasks.map((task) => (
            <li key={task.tehtävä_id}>
              Task: {task.tehtävä_nimi}, Maximum Time: {formatTime(task.max_aika * 1000)}
            </li>
          ))}
        </ul>
      </div>
      <div className="timer">
        <span>{formatTime(elapsedTime * 10)}</span>
      </div>
      <div>
        <h4>Lap Times:</h4>
        {renderTeams()}
      </div>
    </div>
  );
};
const formatTime = (milliseconds) => {
  const totalSeconds = Math.floor(milliseconds / 1000);
  const minutes = Math.floor(totalSeconds / 60);
  const remainingSeconds = totalSeconds % 60;
  const hundredths = Math.floor((milliseconds % 1000) / 10);

  return `${String(minutes).padStart(2, '0')}:${String(remainingSeconds).padStart(2, '0')}:${String(hundredths).padStart(2, '0')}`;
};

export default NewAjastinChild;
