import React, { useState, useEffect } from 'react';

const AlkuEräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data);
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };

  const calculateCombinedTimesByRound = () => {
    const combinedTimesByRound = {};

    times.forEach((time) => {
      const teamId = time.joukkue_id;
      const round = time.era;

      if (!combinedTimesByRound[round]) {
        combinedTimesByRound[round] = {};
      }

      if (!combinedTimesByRound[round][teamId]) {
        combinedTimesByRound[round][teamId] = 0;
      }

      combinedTimesByRound[round][teamId] += time.aika;
    });

    return combinedTimesByRound;
  };

  const findRoundWinners = (combinedTimesByTeam) => {
    const roundWinners = [];
    Object.keys(combinedTimesByTeam).forEach((teamId) => {
      const combinedTime = combinedTimesByTeam[teamId];

      const isWinner = Object.values(combinedTimesByTeam).every(
        (otherCombinedTime) => combinedTime <= otherCombinedTime
      );

      if (isWinner) {
        roundWinners.push({ teamId, combinedTime });
      }
    });

    return roundWinners;
  };

  const findWinnersByRound = () => {
    const combinedTimesByRound = calculateCombinedTimesByRound();
    let allWinners = [];
    let allNonWinners = [];
  
    Object.keys(combinedTimesByRound).forEach((round) => {
      const combinedTimesByTeam = combinedTimesByRound[round];
      const roundWinners = findRoundWinners(combinedTimesByTeam);
  
      allWinners.push(...roundWinners);
  
      // Extract all teamIds for the current round
      const allTeams = Object.keys(combinedTimesByTeam);
  
      // Identify non-winners for the current round
      const nonWinners = allTeams
        .filter((teamId) => !roundWinners.some((winner) => winner.teamId === teamId))
        .map((teamId) => ({ teamId, combinedTime: combinedTimesByTeam[teamId] }));
  
      allNonWinners.push(...nonWinners);
  
      console.log(`Non-winners for ${round}:`, nonWinners);
    });
  
    const sortedNonWinners = allNonWinners
      .map((team) => ({ teamId: team.teamId, combinedTime: team.combinedTime }))
      .sort((a, b) => a.combinedTime - b.combinedTime);
  
    const sixBestNonWinners = sortedNonWinners.slice(0, 6);
  
    const finalWinnersList = [...allWinners, ...sixBestNonWinners];
  
    console.log('Final Winners List:', finalWinnersList);
  
    return { allWinners, nonWinnersExcludingWinners: sixBestNonWinners };
  };
  

  const handleLopetaClick = async () => {
    try {
      const { allWinners, nonWinnersExcludingWinners } = findWinnersByRound();

      console.log('All Winners:', allWinners);
      console.log('All Non-Winners:', nonWinnersExcludingWinners);
    } catch (error) {
      console.error('Error handling lopeta click:', error);
    }
  };

  return (
    <div>
      <button onClick={handleLopetaClick}>Lopeta erä</button>
    </div>
  );
};

export default AlkuEräLopetus;
