import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [formData, setFormData] = useState({
    tehtävä_nimi: '',
    max_aika: '',
  });

  useEffect(() => {
    // Fetch tasks when the component mounts
    fetchTasks();
  }, []);

  const fetchTasks = () => {
    axios.get('http://localhost:3001/api/tehtävät')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  };

  const handleCreateTask = () => {
    axios.post('http://localhost:3001/api/tehtavat', formData)
      .then((response) => {
        console.log('Task created:', response.data);
        setTasks([...tasks, response.data]);
        setFormData({ tehtävä_nimi: '', max_aika: '' });
      })
      .catch((error) => {
        console.error('Error creating task:', error);
      });
  };

  const handleDeleteTask = (taskId) => {
    axios.delete(`http://localhost:3001/api/tehtävät/${taskId}`)
      .then((response) => {
        console.log('Task deleted:', response.data);
        setTasks(tasks.filter((task) => task.id !== taskId));
      })
      .catch((error) => {
        console.error('Error deleting task:', error);
      });
  };

  return (
    <div>
      <h2>Tasks</h2>
      <form>
        <input
          type="text"
          placeholder="Task Name"
          value={formData.tehtävä_nimi}
          onChange={(e) => setFormData({ ...formData, tehtävä_nimi: e.target.value })}
        />
        <input
          type="text"
          placeholder="Max Time"
          value={formData.max_aika}
          onChange={(e) => setFormData({ ...formData, max_aika: e.target.value })}
        />
        <button type="button" onClick={handleCreateTask}>Create Task</button>
      </form>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {task.tehtävä_nimi} - {task.max_aika} hours
            <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Tasks;
