import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TasksSelector({ onSelectTask }) {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3001/api/tehtavat')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }, []);

  const handleTaskSelect = (task) => {
    setSelectedTask(task);
  };

  return (
    <div>
      <h2>Task Selector</h2>
      <select
        value={selectedTask ? selectedTask.tehtävä_id : ''}
        onChange={(e) => {
          const taskId = e.target.value;
          const selected = tasks.find((task) => task.tehtävä_id === taskId);
          handleTaskSelect(selected);
        }}
      >
        <option value="">Select a task</option>
        {tasks.map((task) => (
          <option key={task.tehtävä_id} value={task.tehtävä_id}>
            {task.tehtävä_nimi}
          </option>
        ))}
      </select>
    </div>
  );
}

export default TasksSelector;
