import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function Competition() {
  const [selectedGroup, setSelectedGroup] = useState(null);

  useEffect(() => {
    const socket = io('http://localhost:8000');
  
    socket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });
  
    socket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });
  
    socket.on('selectedGroup', (selectedGroupData) => {
      console.log('Received selected group data:', selectedGroupData);
      setSelectedGroup(selectedGroupData); // No need to parse, assuming it's valid JSON
    });
  
    // Check if there's a selected group in local storage
    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      setSelectedGroup(JSON.parse(storedSelectedGroup)); // Parse the local storage data if it exists
    }
  
    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, []);
  
  return (
    <div>
      <h1>Competition</h1>
      {selectedGroup ? (
        <div>
          <h2>Group Info: {selectedGroup.groupInfo}</h2>
        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
