import React, { useState, useEffect } from 'react';

const AlkuEräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  

  useEffect(() => {
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data);
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };
  

  const calculateCombinedTimesByRound = () => {
    const combinedTimesByRound = {};

    times.forEach((time) => {
      const teamId = time.joukkue_id;
      const round = time.era;

      if (!combinedTimesByRound[round]) {
        combinedTimesByRound[round] = {};
      }

      if (!combinedTimesByRound[round][teamId]) {
        combinedTimesByRound[round][teamId] = 0;
      }

      combinedTimesByRound[round][teamId] += time.aika;
    });

    return combinedTimesByRound;
  };

  const findWinnersAndNonWinnersByRound = () => {
    const combinedTimesByRound = calculateCombinedTimesByRound();
    const winnersByRound = {};
    const allWinners = [];
    let allNonWinners = [];
  
    Object.keys(combinedTimesByRound).forEach((round) => {
      const combinedTimesByTeam = combinedTimesByRound[round];
      const roundWinners = findRoundWinners(combinedTimesByTeam);

      allWinners.push(...roundWinners);
  
      winnersByRound[round] = roundWinners;
  
      const nonWinners = Object.keys(combinedTimesByTeam)
        .filter((teamId) => !roundWinners.some((winner) => winner.teamId === teamId))
        .map((teamId) => ({ teamId, combinedTime: combinedTimesByTeam[teamId], round }));
  
      allNonWinners.push(...nonWinners);
    });
  
    allNonWinners.sort((a, b) => a.combinedTime - b.combinedTime);
  
    return { winnersByRound, allNonWinners };
  };

  

  const findRoundWinners = (combinedTimesByTeam) => {
    const roundWinners = [];
    Object.keys(combinedTimesByTeam).forEach((teamId) => {
      const combinedTime = combinedTimesByTeam[teamId];

      const isWinner = Object.values(combinedTimesByTeam).every(
        (otherCombinedTime) => combinedTime <= otherCombinedTime
      );

      if (isWinner) {
        roundWinners.push({ teamId, combinedTime });
      }
    });

    return roundWinners;
  };

  const handleLopetaClick = async () => {
    try {
      // Fetch and process times
      const { winnersByRound, allNonWinners } = findWinnersAndNonWinnersByRound();
  
      // Extract all winners into a single array
      const allWinners = Object.values(winnersByRound).reduce((acc, roundWinners) => {
        return acc.concat(roundWinners);
      }, []);
  
      // Update 'semifinalist' column for all winners with unique values
      await Promise.all(
        allWinners.map(async (winner) => {
          try {
            const newSemifinalist = await generateUniqueSemifinalist(); // Generate a new unique value
            await updateSemifinalist(winner.teamId, { semifinalist: newSemifinalist });
          } catch (error) {
            console.error(`Error updating semifinalist for team ${winner.teamId}:`, error);
          }
        })
      );
  
      // Update 'kerailyera' column for all non-winners
      await Promise.all(
        allNonWinners.map(async (nonWinner) => {
          try {
            const newKerailyEra = await generateUniqueKerailyEra(); // Generate a new unique value
            await updateJoukkue(nonWinner.teamId, { kerailyera: newKerailyEra });
          } catch (error) {
            console.error(`Error updating kerailyera for team ${nonWinner.teamId}:`, error);
          }
        })
      );
  
      console.log('Updates completed successfully');
    } catch (error) {
      console.error('Error handling lopeta click:', error);
    }
  };
  
  
  
  
  

  return (
    <div>
      <button onClick={handleLopetaClick}>Lopeta erä</button>
    </div>
  );
};

export default AlkuEräLopetus;
